package progres.model;import lombok.Getter;import lombok.Setter;import javax.persistence.*;@MappedSuperclass@Getter@Setter@Inheritance(strategy = InheritanceType.JOINED)public class BaseEntity {    @Id    @GeneratedValue    private int id;    @Override    public String toString() {        return "BaseEntity{" +                "id=" + id +                '}';    }}